mapper.select:
SELECT * FROM tb_book

    AND id = #{id}
    AND book_name = #{bookName}
    AND picture = #{picture}
    AND price = #{price}
    AND num = #{num}
    AND sell_state = #{sellState}

mapper.insert:
INSERT INTO tb_book
    (book_name, picture, price, num, sell_state, sell_num)
VALUES (#{bookName}, #{picture}, #{price}, #{num}, #{sellState}, #{sellNum});

mapper.update:
UPDATE tb_book SET
AND book_name = #{bookName}, AND picture = #{picture}, AND price = #{price}, AND num = #{num}, AND sell_state = #{sellState}, AND sell_num = #{sellNum},
WHERE id = #{id};
