mapper.select:
SELECT * FROM tb_book

    AND id = #{id}
    AND book_name = #{bookName}
    AND picture = #{picture}
    AND price = #{price}
    AND sellTime = #{sellTime}
    AND num = #{num}
    AND sell_state = #{sellState}

mapper.insert:
INSERT INTO tb_book
    (book_name, picture, price, sell_time, num, sell_state)
VALUES (#{bookName}, #{picture}, #{price}, #{sellTime}, #{num}, #{sellState});

mapper.update:
UPDATE tb_book SET
AND book_name = #{bookName}, AND picture = #{picture}, AND price = #{price}, AND picture = #{sellTime}, AND picture = #{num}, AND sell_state = #{sellState},
WHERE id = #{id};
